<?php
global $current_user;
$user_id   = $current_user->ID;
$error     = array();
$alertinfo = array();
$alert     = array();
$alertmail = array();
if ( isset( $_POST['updatepass'] ) ) {

	$password      = sanitize_text_field( $_POST['password'] );
	$conf_password = sanitize_text_field( $_POST['conf_password'] );

	if ( strcmp( $password, $conf_password ) !== 0 ) {
		$error['error_msg'] = esc_html__( "Password didn't match", 'resido' );
	}

	if ( count( $error ) == 0 ) {
		// Update user password
		$user_update = wp_set_password( $password, $user_id );

		// Check if theres any error else return success
		if ( ! is_wp_error( $user_update ) ) {
			$alert['class'] = 'success';
			$alert['msg']   = esc_html__( 'Profile Password Successfully Updated.', 'resido' );
		}
	} else {
		$alert['class'] = 'danger';
		$alert['msg']   = $error['error_msg'];
	}
}
?>
